$(function() {
 
    // ボタンをクリックしたら発動
    $('#btn').click(function() {
   
      // ①テキストボックスに入力した都市名を変数に格納
      var addTag = $('#addTag').val();
   
      // ②appendで動的にチェックボックスを追加
      $('.citys').append('<input type="checkbox" name="city[]" value="'+addTag+'">：'+addTag);
   
    });

  });
